
public class Math2 {

	public static final double PI = 3.;
	public static final double E = 2.;
	
	/**
	 * Determines max of a vs b
	 * @param a, double
	 * @param b, double
	 * @return max
	 */
	public static double max(double a, double b) {
		if(a>b)
			return a;
		else return b;
	}
	/**
	 * Determines max of a vs b
	 * @param a, double
	 * @param b, int
	 * @return max
	 */
	public static int max(int a, int b) {
		if(a>b)
			return a;
		else return b;
	}
	
	/**
	 * Determines Min of a vs b
	 * @param a, double
	 * @param b, double
	 * @return min
	 */
	public static double min(double a, double b){
		if(b>a)
			return a;
		else return b;
	}
	
	/**
	 * Determines Min of a vs b
	 * @param a, int
	 * @param b, int
	 * @return min
	 */
	public static int min(int a, int b){
		if(b>a)
			return a;
		else return b;
	}
	
	/**
	 * multiplies a by itself b times
	 * @param a, what we're multiplying by
	 * @param b, iterations
	 * @return String, the response
	 */
	public static String pow(int a, int b) {
		int response = a;
		for(int i=1;i<b;i++){
			response *= a;
		}
		return Integer.toString(response);
	}

}
